   <link rel="stylesheet" href="<?php echo base_url(); ?>assets/dist/css/bootstrapValidator.css"/>    <script src="<?php echo base_url(); ?>assets/dist/js/bootstrapValidator.js"></script> <!--main content start-->  <section id="main-content">    <section class="wrapper">      <div class="row">        <div class="col-lg-12">          <h3 class="page-header"><i class="fa fa-users" aria-hidden="true"></i>Add Category</h3>         <ol class="breadcrumb">            <li><i class="fa fa-home"></i><a href="<?php echo base_url();?>admin/dashboard">Home</a></li>            <li><i class="fa fa-users" aria-hidden="true"></i>Categories</li>           <!-- <li><i class="fa fa-square-o"></i>Starters</li>-->          </ol>        </div>      </div>      <div class="row">        <div class="col-lg-12">          <section class="panel">            <header class="panel-heading"> Add Category </header>            <div class="panel-body">              <form role="form" enctype="multipart/form-data" onsubmit="return validationcheck();" id="addcategory" name="addcategory" action="<?php echo base_url('admin/categories/insert'); ?>" method="post">              <div><?php echo $this->session->flashdata('message');?></div>                <div class="form-group nopaddingRight col-md-6 san-lg">                  <label for="exampleInputEmail1">Category</label>                  <input type="text" name="category_name" id="category_name" value="<?php echo set_value('category_name'); ?>" class="form-control">                      <span style="color:red"> <?php echo form_error('category_name'); ?> </span>						<span id="caterrormsg" style="color:red"> </span>				</div>				<div class="form-group nopaddingRight col-md-6 san-lg">                  <label for="exampleInputEmail1">Document</label>                  <input type="file" name="category_file" id="category_file" value="" class="form-control">                      <span style="color:red"> <?php echo form_error('category_name'); ?> </span>                      <span id="errormsg" style="color:red"> </span>                </div>                               <div class="clearfix"></div>                <button type="submit" class="btn btn-primary">Submit</button>                <button type="submit" class="btn btn-danger" onclick="window.location='<?php echo base_url('admin/categories'); ?>'">Cancel</button>              </form>            </div>          </section>        </div>      </div>      <!-- page start-->       <!-- page end-->     </section>  </section>  <!--main content end--> 	<script type="text/javascript">function validationcheck(){		var catname=document.getElementById('category_name').value;	if(catname==''){	jQuery('#caterrormsg').html('Plese enter category name');	return false;	}else{	jQuery('#caterrormsg').html('');		}	 var file = document.getElementById('category_file').value;	 var fup = document.getElementById('category_file');	 if(file==''){		jQuery('#errormsg').html('Please select Category File');		return false;	}else{		jQuery('#errormsg').html('');	}		var fileName = fup.value;		var ext1 = fileName.substring(fileName.lastIndexOf('.') + 1);		if(ext1 !=''){		if(ext1 == "xlsx" || ext1 == "xls")		{		jQuery('#errormsg').html('');		} else{		jQuery('#errormsg').html('Uploaded file is not a valid. Only xlsx,pdf files are allowed');		return false;		}		}}</script>